mapper.insert:
insert into t_stu(id, name, gender, college, major, password) values(
    #{id}, #{name}, #{gender}, #{college}, #{major}, "123456"
);

mapper.update:
update t_stu
    set name = #{name}, gender = #{gender}, address = #{address}, email = #{email}, tel = #{tel}, college = #{college}, major = #{major}
    where id = #{id};
